import { Ref } from 'vue';
export type PopupType = 'popup' | 'dialog' | 'message' | 'drawer';
export default function usePopupManager(type: PopupType, { visible, runOnMounted, }?: {
    visible?: Ref<boolean>;
    runOnMounted?: boolean;
}): {
    zIndex: Readonly<Ref<number>>;
    open: () => void;
    close: () => void;
    isTopInteractivePopup: () => boolean;
};
